    {
        MyClass intendedChanges = conflicts.ReadConflictContent<MyClass>(item);
        ItemResponse<MyClass> currentState = await conflicts.ReadCurrentAsync<MyClass>(intendedChanges.MyPartitionKey, item);
    }
}


